(147, 1, 'Passenger Car(PC)_Brand_ Ignite_Sealed Maintenance (SMF) Battery', '<p><strong>Product Info:</strong><br />\r\nIGNITE is a Sealed Maintenance Free (SMF) Battery for Passenger Cars (PC) which is the ultimate in battery performance and technology. Featuring special internal components, unique design features and leading technologies to deliver what car enthusiasts really want.</p>\r\n\r\n<p><strong>Features and Benefits:</strong></p>\r\n\r\n<ul>\r\n	<li><a href=\"https://www.saifpowertecltd.com/ignite-pc-smf.php\">Special Calcium alloy makes the battery capability superior for Maintenance Free (MF) operation.</a></li>\r\n	<li>Advanced grid design improves the adherence of active material for corrosion resistance and longer life of battery.</li>\r\n	<li>Automated Cast on strap (COS) machine is used for group burning.</li>\r\n	<li>Advanced grid design improves the adherence of active material for corrosion resistance and longer life of battery.</li>\r\n	<li>Specified thickness and weight of plate provides proper service life and operation.</li>\r\n	<li>High porosity and very low resistance PEGM Separator reduces the internal resistance of battery and ensure proper utilization of electrolyte and Battery service life.</li>\r\n	<li>Manufactured with new, advanced and world class European and USA machineries.</li>\r\n	<li>First time in Bangladesh use of modern methodology of Lead Testing by ICP-OES for best impurity control.</li>\r\n	<li>Use of special chemical improves the cold cranking performance.</li>\r\n	<li>No scope of spillage hazard.</li>\r\n	<li>Robust design of Container and Cover makes the battery life more stronger and longer for use in rough terrain.</li>\r\n	<li>Low shelf discharge rate &amp; long shelf life.</li>\r\n	<li>High operational reliability.</li>\r\n	<li>No need to add water in life time.</li>\r\n	<li>Time saving and hassle free.</li>\r\n</ul>\r\n', 'product_147.png', '', '', 'IGNITE', 1, NULL),
(148, 1, 'Passenger Car(PC)_Brand_ Express _Low Maintenance (LM) Battery', '<p><strong>Features and Benefits:</strong></p>\r\n\r\n<ul>\r\n	<li><a href=\"https://www.saifpowertecltd.com/spb-pc.php\">Special alloy composition reduces the gassing and frequent topping up of electrolyte, in turns makes the battery almost maintenance free.</a></li>\r\n	<li>Advanced grid design improves the adherence of active material makes the battery corrosion resistance and ensures longer life of battery.</li>\r\n	<li>Use of Automated Cast On Strap (COS) machine for group burning.</li>\r\n	<li>Thick flat plate with superior edge over competitors.</li>\r\n	<li>High porosity and very low resistance poly ethylene Separator reduces the internal resistance of battery.</li>\r\n	<li>Manufactured with new, advanced and world class European and USA machineries.</li>\r\n	<li>First time in Bangladesh use of modern methodology of Lead Testing by ICP-OES for best impurity control.</li>\r\n	<li>Use of special chemical improves the cold cranking performance.</li>\r\n	<li>Manufactured with new, advanced and world class European and USA machineries.</li>\r\n	<li>First time in Bangladesh use of modern methodology of Lead Testing by ICP-OES for best impurity control.</li>\r\n	<li>Use of special chemical improves the cold cranking performance.</li>\r\n	<li>Robust design of Container and Cover makes the life of battery more stronger and longer for use in rough terrain.</li>\r\n	<li>Low shelf discharge rate &amp; long shelf life.</li>\r\n	<li>High operational reliability.</li>\r\n</ul>\r\n', 'product_148.png', '', '', 'Passenger Car', 1, NULL),
